mapper.select:
select
bde.dataid as dataid,
bde.name as name,
bde.definition as definition,
bde.send_date as sendDate,
bde.confirm_date as confirm_date,
dde.source_unit_name as collectDeptName,
bde.source_unit_code as sourceUnitCode,
bde.source_unit_name as sourceUnitName,
bde.status as status
from base_data_element bde
         left join domain_data_element dde on bde.dataid = dde.base_dataelement_dataid

    bde.collectunitqty = 1

        and dde.source_unit_code in

            #{item}



        and bde.status in

            #{item}



        and (bde.name like #{dto.search} or bde.definition like #{dto.search}
        or bde.source_unit_name like #{dto.search} or dde.source_unit_name like #{dto.search}
        )


        and bde.send_date between #{dto.initiationTimeStart} and #{dto.initiationTimeEnd}


        and bde.confirm_date between #{dto.sourceTimeStart} and #{dto.sourceTimeEnd}



    order by ${sortSql}

select bde.data_element_id as dataElementId, bde.dataid as dataid, bde.datatype as datatype, bde.definition as definition, bde.send_date as receiveTime, bde.name as name, ct.status as status, ct.processing_date as processingDate from base_data_element bde left join confirmation_task ct on bde.dataid = ct.base_dataelement_dataid where ct.processing_unit_code = #{organCode,jdbcType=VARCHAR}
and ct.status = 'pending'
and bde.send_date between #{dto.receiveTimeStart} and #{dto.receiveTimeEnd}
and ct.processing_date between #{dto.handleTimeStart} and #{dto.handleTimeEnd}
and ct.status in
#{item}
and (bde.name like concat('%', #{dto.serarch}, '%') or bde.definition like concat('%', #{dto.serarch}, '%'))
order by ${sortSql}

SELECT bdl.dataid, bdl.name, bdl.definition, bdl.status, ct.processing_unit_code AS paUnitCode, ct.processing_unit_name AS paUnitName, bdl.send_date AS sendDate FROM base_data_element bdl LEFT JOIN confirmation_task ct ON bdl.dataid = ct.base_dataelement_dataid AND (ct.status in ('claimed', 'confirmed')) WHERE bdl.status = 'pending_approval'
AND bdl.send_date >= #{dto.sendDateStart}
AND bdl.send_date <= #{dto.sendDateEnd}
AND (bdl.name LIKE CONCAT('%', #{dto.search}, '%') OR bdl.definition LIKE CONCAT('%', #{dto.search}, '%') OR ct.processing_unit_name LIKE CONCAT('%', #{dto.search}, '%'))
ORDER BY ${sortSql}
ORDER BY ct.processing_date DESC

SELECT dataid, name, definition, status, source_unit_code AS sourceUnitCode, source_unit_name AS sourceUnitName, source_unit_code AS paUnitCode, source_unit_name AS paUnitName, send_date AS sendDate, confirm_date AS confirmDate, collectunitqty FROM base_data_element bdl WHERE status = 'confirmed'
AND send_date >= #{dto.sendDateStart}
AND send_date <= #{dto.sendDateEnd}
AND confirm_date >= #{dto.confirmDateStart}
AND confirm_date <= #{dto.confirmDateEnd}
AND source_unit_code IN
#{unitCode}
AND (name LIKE CONCAT('%', #{dto.search}, '%') OR definition LIKE CONCAT('%', #{dto.search}, '%') OR source_unit_name LIKE CONCAT('%', #{dto.search}, '%'))
ORDER BY ${sortSql}
ORDER BY bdl.confirm_date DESC
