mapper.select:
select deb.data_element_id, data_element_name, data_element_definition, data_element_data_type, data_element_data_format, data_element_status from data_element_base deb
and (deb.data_element_name like concat('%', #{dto.searchValue}, '%') or deb.data_element_definition like concat('%', #{dto.searchValue}, '%'))
and deb.data_element_id in ( select data_element_id from data_element_belong_category where data_element_category_code in
#{categoryId}
)
and deb.data_element_id in ( select data_element_id from data_element_collectorg where data_element_collect_org_code in
#{orgCode}
)
and deb.data_element_id in ( select data_element_id from data_element_collectorg where dept_code_or_area_code in
#{regionCode}
)
and deb.data_element_status = #{dto.status}
order by deb.create_date desc
